import { useEffect, useState } from "react"
import axios from 'axios'
export  default function MyAccount(){
    const[error,setError]=useState('')
    const[myDetail,setMyDetail]=useState({})
    console.log('my',myDetail)
    useEffect(()=>{
        
        if(localStorage.getItem('token')){
            (async()=>{
                try{
                    const response=await axios.get('http://localhost:3044/api/account',{
                        headers:{
                            Authorization:localStorage.getItem('token')
                        }
                    })
                    // console.log("detail",response.data)
                    setMyDetail(response.data)
                    setError('')
                }catch(err){
                    setError("server error")
                    console.log(err)
                }
            })()
        }else{
            setError("login ise require")
        }
    },[])
    return(
        <div className="container">
            <h2>My Details</h2>
       {error ? <div> <p>{myDetail.username}</p>{myDetail.email}<p>{myDetail.role}</p></div> : error}
      </div> 
    )
}
